import React, { memo, useEffect, useState } from "react";
import Slider from "react-slick";
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { settings } from "./constants";
import CustomSlide from "./CustomSlide";
import api from "../../core/api";
import axios from "axios";
import styled from 'styled-components';
import { Spin } from "antd";
import { shuffleArray } from '../../store/utils';

const StyledSpin = styled(Spin)`
  .ant-spin-dot-item {
    background-color: #FF343F;
  }
  .ant-spin-text {
    color: #FF343F;
  }
`
const HotCollections = () => {
  const MAX_COUNT = 5;
  const [hotCollections, setHotCollections] = useState([]);

  const [loading, setLoading] = useState(true);

  const defaultAvatar = api.baseUrl + '/uploads/thumbnail_author_4_623046d09c.jpg';
  const fallbackImg = 
    "data:image/png;base64,";

  async function getCollections() {
    try {
      await axios.get(`${process.env.REACT_APP_SERVER_URL}/api/collection/all`, {
        headers: {
          'Content-Type': 'application/json',
        },
        params: {}
      }).then(async res => {
        let collections = shuffleArray(res.data.collections);
        let count = collections.length > MAX_COUNT ? MAX_COUNT : collections.length;

        for (let i = 0; i < count; i++) {
          let c = collections[i];

          //get user info
          let author = null;
          await axios.get(`${process.env.REACT_APP_SERVER_URL}/api/user`, {
            headers: {
              'Content-Type': 'application/json',
            },
            params: {
              walletAddr: c.walletAddr.toLowerCase()
            }
          }).then(res1 => {
            author = res1.data.user;
          });

          c.author = author;

          setLoading(false);
        }

        setHotCollections(collections);
      });
    } catch {
      console.log('error in fetching collections');
    }
  }
  
  useEffect(() => {
    getCollections();
  }, [settings]);

  return (
      <div className='nft'>
        <StyledSpin tip="Loading..." size="large" spinning={loading}/>
        { hotCollections.length > 0 &&
        <Slider {...settings}>
          { hotCollections && hotCollections.map((item, index) => (
            <CustomSlide
              key={index}
              index={index + 1}
              avatar={item.author && item.author.avatar ? item.author.avatar : defaultAvatar}
              banner={item.image ? item.image : fallbackImg}
              username={item.title}
              uniqueId={item.category}
              collectionId={item.collectionAddr}
            />
          ))}
        </Slider>
        }
      </div>
  );
};

export default memo(HotCollections);
